import { reactive, provide, inject } from 'vue';

// Tạo một đối tượng reactive để lưu trữ người dùng
const userState = reactive({
  user: null,
});

// Hàm để đặt người dùng mới
const setUser = (newUser) => {
  userState.user = newUser;
};

// Hàm để lấy người dùng hiện tại
const getUser = () => {
  return userState.user;
};

// Provide UserContext để sử dụng ở các thành phần khác
const UserContextSymbol = Symbol();
const provideUserContext = () => {
  provide(UserContextSymbol, {
    setUser,
    getUser,
  });
};

// Inject UserContext vào các thành phần khác
const useUserContext = () => {
  const userContext = inject(UserContextSymbol);
  if (!userContext) {
    throw new Error('useUserContext must be used within a component wrapped in provideUserContext.');
  }
  return userContext;
};

export {
  provideUserContext,
  useUserContext,
};
