import { ref } from 'vue';
import VueCookies from 'vue-cookies';

const myUser = ref(null);

const loginUser = (payload) => {
  VueCookies.set('token', payload.token);
  VueCookies.set('user', JSON.stringify(payload.user));
  myUser.value = payload;
};

const logoutUser = () => {
  VueCookies.remove('token');
  VueCookies.remove('user');
  myUser.value = null;
};

export { myUser, loginUser, logoutUser };
